// Generate data
// let dottedBase = +new Date();
// let oneDay = 3600 * 1000;
let tideAmountData = [];
let sign = -1;
for (let i = 0; i < 8; i++) {
    // let date = new Date((dottedBase + oneDay * i));
    let b = Math.random() * 200;
    tideAmountData.push(b * sign);
    sign *= -1;
}

let dottedBase = +new Date();
let oneHour = 3600 * 1000;
let tideSpeedData = [];
let tideSpeedDataB = [];
let tideDirectDataB = [];
let tideDirectDataA = [];
for (let i = 0; i < 30; i++) {
    let date = new Date(dottedBase + oneHour * i);
    let b = Math.random();
    let c = Math.random();
    let a = Math.random();
    let d = Math.random();
    tideSpeedData.push([+date, b * 1.5])
    tideSpeedDataB.push([+date, c * 1.5])
    tideDirectDataA.push([+date, a * 360])
    tideDirectDataB.push([+date, d * 360])
}

var data = [];
// Parametric curve
for (var t = 0; t < 25; t += 0.001) {
    var x = (1 + 0.25 * Math.cos(75 * t)) * Math.cos(t);
    var y = (1 + 0.25 * Math.cos(75 * t)) * Math.sin(t);
    var z = t + 2.0 * Math.sin(75 * t);
    data.push(['S800', y, z]);
}

